import React, { Component } from 'react'
import Item from './components/Item'
import ResponseType from '../connection/ResponseType'
import Connection from '../connection/Connection'

class Received extends Component {
    constructor(props) {
        super(props)

        this.state = {
            loading: true,
            content: [],
            errorMessage: '',
            error: false
        }

        this.setupSocket = this.setupSocket.bind(this)
        this.setContent = this.setContent.bind(this)
        this.query = this.query.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    query() {
        const msg = {
            id: sessionStorage.getItem('id'),
            type: ResponseType.DONATIONS_RECEIVED
        }

        if (this.socket.readyState === this.socket.OPEN)
            this.socket.send(JSON.stringify(msg))
        else setTimeout(this.query, 10)
    }

    setContent(content) {
        this.setState({
            error: false,
            loading: false,
            content: JSON.parse(content)
        })
    }

    setupSocket() {
        this.socket = Connection

        this.socket.onmessage = (r) => {
            const response = JSON.parse(r.data)
            switch (response.type) {
                case ResponseType.SUCCESS:
                    this.setContent(response.message)
                    break
                case ResponseType.FAIL:

                    break
                default:
                    this.setState({
                        error: true,
                        errorMessage: 'Erro: Indefinido.'
                    })
            }
        }        
    }

    componentDidMount() {
        this.setupSocket()
        this.query()
    }

    validateDonation() {
        if (this.socket.readyState !== this.socket.OPEN) 
            setTimeout(this.validateDonation, 10)


    }

    handleSubmit(event) {
        event.preventDefault()
        this.validateDonation()
    }

    render() {
        const { content } = this.state

        const donations = content.map((donation, index) => 
            <form key={index} name={'donation' + donation.id} onSubmit={this.handleSubmit}>
                <Item data={donation} />
                <input type='submit' value='Confirmar recebimento'></input>
            </form>
        )

        return (
            this.state.error ?
            <p>{this.state.errorMessage}</p> :
            this.state.loading ?
            <p>Loading data...</p> :
            <div>
                {donations}
            </div>
        )
    }
}

export default Received
