import React, { Component } from 'react'
import Needs from './components/Needs'

class EditCharity extends Component {
    constructor(props) {
        super(props)
        this.state = {
            images: [],
            previews: []
        }

        this.handleImages = this.handleImages.bind(this)
    }

    handleSubmit(event) {
        event.preventDefault()
        switch(event.target.name) {
            case 'needs':

                break
            case 'description':

                break
            case 'photos':
                break

            default:
        }
    }

    handleChange(event) {
        this.setState({
           [event.target.name]: event.target.value
        })
    }

    handleImages(event) {
        console.log(event.target.files)
        this.setState({
            images: event.target.files,
            previews: Array
                .from(event.target.files)
                .map((image) => 
                    URL.createObjectURL(image)
                )
        })



    }

    pickImg(image) {
        console.log(image)
    }

    render() {
        const arrayImages = Array.from(this.state.images)
        const nameImages = arrayImages.map((image, index) => 
            <div>
                <p key={index}>{image.name}</p>
            </div>
        )

        console.log(this.state.previews)
        const previewImages = this.state.previews.map((src, index) => 
            <img src={src} key={index}/>
        ) 

        return (
            <div>
                <h2>Descrição</h2>
                <textarea placeholder='Escreva a descriçao'></textarea>

                <h2>Fotos</h2>
                <form name='photos' onSubmit={this.handleSubmit}>
                    <label style={{border: '1px solid'}}>
                        Selecionar imagens
                        <input style={{display: 'none'}} onChange={this.handleImages} type='file' accept="image/png, image/jpeg" multiple/>
                    </label>
                    <p><b>Selecionadas</b></p>
                    {nameImages}
                    {previewImages}
                    <input type='submit' value='Enviar fotos'/>
                </form>

                <h2>necessidades</h2>
                <Needs data={[]}/>
                <form name='needs' onSubmit={this.handleSubmit}>
                    <input onChange={this.handleChange} name='item' type='text' placeholder='Item'/>
                    <input onChange={this.handleChange} name='amount' type='number' placeholder='Quantidade' min='1'/>
                    <input type='submit' value='Adicionar necessidade'/>
                </form>

            </div>
        )
    }
}

export default EditCharity
