           [event.target.name]: event.target.value
        })
    }

    handleImages(event) {
        console.log(event.target.files)
        this.setState({
            images: event.target.files
        })
    }

    pickImg(image) {
        console.log(image)
    }

    render() {

        const { images } = this.state
        const arrayImages = Array.from(images)
        const selectedImages = arrayImages.map((image, index) => 
            <p key={index}>{image.name}</p>
        )

        return (
            <div>
                <h2>Descrição</h2>
                <textarea placeholder='Escreva a descriçao'></textarea>

                <h2>Fotos</h2>
                <form>
                    <label> <button>Selecionar imagens</button>
                        <input onChange={this.handleImages} type='file' accept="image/png, image/jpeg" multiple/>
                    </label>
                    {selectedImages}
                </form>

                <h2>necessidades</h2>
                <Needs data={[]}/>
                <form name='needs' onSubmit={this.handleSubmit}>
                    <input onChange={this.handleChange} name='item' type='text' placeholder='Item'/>
                    <input onChange={this.handleChange} name='amount' type='number' placeholder='Quantidade' min='1'/>
                    <input type='submit' value='Adicionar necessidade'/>
                </form>

            </div>
        )
    }
}

export default EditCharity
