import React, {Component} from 'react'
import Needs from './components/Needs'
import Connection from '../connection/Connection'
import ResponseType from '../connection/ResponseType'

class Charity extends Component {
    constructor(props) {
        super(props)

        this.state = {
            loading: true,
            content: {},
            errorMessage: '',
            error: false
        }

        this.query = this.query.bind(this)
        this.setContent = this.setContent.bind(this)
    }

    query() {
        const msg = {
            id: this.props.match.params.id,
            type: ResponseType.CHARITY
        }

        if (this.socket.readyState === this.socket.OPEN)
            this.socket.send(JSON.stringify(msg))
        else setTimeout(this.query, 10)
    }

    setContent(content) {
        this.setState({
            error: false,
            loading: false,
            content: JSON.parse(content)
        })
    }

    setError(error) {
        this.setState({
            errorMessage: error,
            error: true
        })
    }

    setupSocket() {
        this.socket = Connection

        this.socket.onmessage = (r) => {
            const response = JSON.parse(r.data)
            console.log(response)
            switch (response.type) {
                case ResponseType.SUCCESS:
                    this.setContent(response.message)
                    break
                case ResponseType.FAIL:
                    this.setError(response.message)
                    break
                default:
                    this.setState({
                        error: true,
                        errorMessage: 'Error: Undefined error'
                    })
            }
        }        
    }

    componentDidMount() {
        this.setupSocket()
        this.query()
    }

    render() {
        const { content } = this.state

        console.log(content.needs.needs)
        // if (content)
        //     const needsSelector = content.needs.map((need, index) => 
        //         <label>
        //             <input key={index} type='radio' name={need.name}  />
        //             {need.name}
        //         </label>
        //     )

        return (
            this.state.error ?
            <p>{this.state.errorMessage}</p> :
            this.state.loading ?
            <p>Loading data...</p> :
            <div>
                <h1>{content.name}</h1>
                <h2>{content.field}</h2>
                <p>{content.description}</p>

                <h2>Lista de nec.</h2>
                {/* <Needs data={content.needs} /> */}
                {needsSelector}
            </div>
        )
    }
}

export default Charity
