import React, { Component } from 'react'

class Login extends Component {
    constructor(props) {
        super(props)

        this.state = {
            error: ''
        }

        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    handleSubmit(event) {
        event.preventDefault()
        const { username, senha } = this.state
        if (username && senha) {

        } else {
            this.setState({
                error: 'Insira o nome e a senha'
            })
        }
    } 

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value
        })
    }


    render() {
        return (
            <div>
                <h2>Logar</h2>
                <p>{this.state.error}</p>
                <form name='login' onSubmit={this.handleSubmit}>
                    <input onChange={this.handleChange} name='username' type='text' placeholder='username'></input>
                    <input onChange={this.handleChange} name='password' type='password' placeholder='senha'></input>
                    <input type='submit' value='login'></input>
                </form>
            </div>
        )
    }
}

export default Login
